  }

  function generateNote(note, index) {
    return <Note key={index} noteHeader={note.h} noteContent={note.p} />; // key property should be replaced by a unique id instead of index
  }

  return (
    <div>
      <NewNote onAdd={addNewNote(props)} />
      {notes.map(generateNote)}
    </div>
