import React, { useState } from "react";

function Note(props) {
  const state = useState();

  const [headingText, setHeadingText] = useState("Hello");
  const [buttonClass, setButtonClass] = useState("button-idle");
  const [userInput, setUserInput] = useState("Type bellow")

  function handleClick() {
    setHeadingText("Submittted");
  }

  function handleMouseOver() {
    setButtonClass("button-hover");
  }

  function handleMouseOut() {
    setButtonClass("button-idle");
  }

  function handleChange(event) {
    console.log(event.target.value)
  }

  return (
    <div className="note">
      <h1>{props.h}</h1>
      <h2>{state[0]}</h2>
      <div className="note-img-div">
        <img src={props.imgURL}></img>
      </div>
      <h2>{headingText}</h2>
      <input onChange={handleChange} type="text"></input>
      <button
        className={buttonClass}
        onClick={handleClick}
        onMouseOver={handleMouseOver}
        onMouseOut={handleMouseOut}
      >
        Submit
      </button>
      <p>{props.p}</p>
    </div>
  );
}

export default Note;
